#define L(x,y) length(i + vec2(x, y))
void mainImage (out vec4 o, vec2 i)
{
    i /= iResolution.x;
    i -= vec2(.5, .3);
    i *= 5.;
    i = L(0, 1) < 2. && L(0, -1) < 2. || L(1, 0) > 3. || L(-1, 0) > 3. ? i : i.yx;

    i.x = abs(i.x) + 1.;
    i = vec2(length(i), atan(i.x, i.y)) * 5.;
    i.y += floor(i.x) * iTime * .1;

    o = abs(sin(i.y + vec4(0, .3, .7, 1)) * fract(i.x));
}
