void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 vv = fragCoord.xy - iResolution.xy / 2.0;
	vv.x += sin( iTime * 2.0 ) * 4.0 * (length(vv) / 15.0);
	vv.y += cos( iTime * 2.3 ) * 3.0 * (length(vv) / 15.0);

	vec4 a = vec4( .0, .7, .2, 1.0 );
    vec4 b = vec4( .3, .0, .4, 1.0 );
    vec4 color = mix(a, b, smoothstep(-0.1, 0.1, sin( atan( vv.y, vv.x ) * 10.0 + iTime * 10.0 + length(vv) / 10.0 )));
	color.xyz *= length(vv) / (length(iResolution.xy)/3.0);
	fragColor = color;
}
