void circle(vec2 loc, vec2 fragCoord, out vec4 fragColor){
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 screenCoord = fragCoord.xy;

	float rad = 100.0;

	vec2 toMiddle = iResolution.xy * 0.5 - screenCoord - loc;
	float lengthToMiddle = toMiddle.x * toMiddle.x + toMiddle.y * toMiddle.y;
	if(lengthToMiddle > rad * rad)
	{
		fragColor = vec4(0,0,1,0);
	}
	else{
		float lm = 1.0-(length(lengthToMiddle)/length(rad*rad));
		fragColor = vec4(lm,lm,lm, 1);
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	circle(vec2(0,100.0*sin(iTime/1.0)), fragCoord, fragColor);

}
