#define M_PI 3.1415926535897932384626433832795

/**
 * Draw a circle at vec2 `pos` with radius `rad` and
 * color `color`.
 */
vec4 circle(vec2 pos, float rad, vec3 color) {
    // Get the distance of the current pixel from pos
	float distance = length(pos);

    // Get the difference between the distance and the radius
    float drad = distance - rad;

	// Constrain the difference to the range 0.0 - 1.0.
	// Pixels > rad away will end up with a value of 1.0, whereas
	// pixels < rad away will get 0.0.
	float t = clamp(drad, 0.0, 1.0);

    // Get the alpha for the pixel. Pixels > rad away get
    // an alpha of 0.0, those within the radius get an
    // alpha of 1.0.
    float alpha = 1.0 - t;

	return vec4(color, alpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 center = iMouse.xy - fragCoord.xy;

    float radius = 0.1 * iResolution.y;

    // Make the circle "pulse"
    float pulse_frequency = 2.0; // Hz
    float pulse_factor = sin(iTime * M_PI * 2.0 * pulse_frequency);
    radius = radius + radius * 0.05 * pulse_factor;

    // Background layer
    vec3 gray = vec3(0.75, 0.75, 0.75);
	vec4 background = vec4(gray, 1.0);

	// Circle
	vec3 red = vec3(1.0, 0.0, 0.0);
	vec4 c = circle(center, radius, red);

	// Blend the two
	fragColor = mix(background, c, c.a);

}
