void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set up our coordinate system
    float aspectRatio = iResolution.y / iResolution.x;
    vec2 uv = ((fragCoord.xy / iResolution.xy) + vec2(0.5,0.5));
    uv.y *= -aspectRatio;

    // clamp the u axis to the center of a pixel.
    // iChannelResolution0 doesn't work for me for some reason?
    if (uv.x > 1.0) {
        uv.x -= 0.5;    
    	const float textureResolution = 64.0;
    	//uv.x = floor(uv.x * textureResolution) / textureResolution;
    	uv.y = floor(uv.y * textureResolution) / textureResolution;
    }

    // sample texture
    vec3 color  = texture(iChannel0, uv).xyz;

    // set the final color
	fragColor = vec4(color,1.0);    
}
