void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float scale = iResolution.y / iResolution.x;
	const int maxIterations = 500;

    int ii = 0,iii = 0;
    uv += vec2(-0.5,-0.5);
    uv *= 5.; 
	uv.y*=scale;

    float xx = -2. +4. *(iMouse.x / iResolution.x);
    float yy = ceil((-2. +4. *(iMouse.y / iResolution.y)) *20.)/20. ;

    //xx = 0.25209;
    //yy = 0.00;

	vec2 z = uv, z2 = uv;
	vec3 color;

	//Julia set
    for(int i=0; i<maxIterations; i++)
	{
		z = vec2(z.x*z.x - z.y*z.y + xx , 2.*z.x*z.y + yy);
        ii = i;

        if(z.x*z.x+z.y*z.y >=4.)
            break;
	}

    // Mandelbrot set 
    for(int i=0; i<maxIterations; i++)
	{
		z2 = vec2(z2.x*z2.x - z2.y*z2.y, 2.*z2.x*z2.y) +uv;
        iii = i;

        if(z2.x*z2.x+z2.y*z2.y >=4.)
            break;
	}

    float sco = float(ii) +1. - log2(log2(dot(z,z)));
	float angle = float(sco) / 11.;

	color.r = sin(angle*1.) * 1.;
	color.g = sin(angle*2.) * 1.;
	color.b = sin(angle*3.) * 1.;

    if(ii >= maxIterations -1)
        color=vec3(1.,1.,1.);

    // Mandelbrot outline
    if(iii < (maxIterations -1) && maxIterations -460 < iii)
        color=vec3(1.,1.,1.);
    if(iii < (maxIterations -2) && maxIterations -380 < iii)
        color=vec3(0.,0.,0.);

	fragColor = vec4(color,1.0);

}
