float spiral(float angle, float radius, float frequency, float rate)
{
	return fract(0.3 * radius * frequency + rate * iTime + angle);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = fragCoord.xy - (iResolution.xy * 0.5);
    float angle = atan(pos.y, pos.x) / (2.0 * 3.14159);
    float radius = sqrt(dot(pos, pos));
    float spiral1 = spiral(angle, radius, 10.0 + 0.1 * sin(0.5 * iTime), 2.0);
    float spiral2 = spiral(angle, radius, 20.0 + 0.05 * cos(0.7 * iTime), -1.9);
    float spiral3 = spiral(angle, radius, 15.0, 0.5 * sin(0.84 * iTime));
	fragColor = vec4(spiral1, spiral2, (1.0 - spiral1 - spiral2), 1.0);
}
