const int Samples = 64; //multiple of 2
float Intensity = 0.1;

const bool UseNoise = false;
const float NoiseScale = 1.0;
const float NoiseStrength = 0.1;

vec4 DirectionalBlur(in vec2 UV, in vec2 Direction, in float Intensity, in sampler2D Texture)
{
    vec4 Color = vec4(0.0);  
    float Noise = texture(iChannel1,UV*NoiseScale).x-0.485;

    if (UseNoise==false)
    for (int i=1; i<=Samples/2; i++)
    {
    Color += texture(Texture,UV+float(i)*Intensity/float(Samples/2)*Direction);
    Color += texture(Texture,UV-float(i)*Intensity/float(Samples/2)*Direction);
    }
	else      
    for (int i=1; i<=Samples/2; i++)
    {
    Color += texture(Texture,UV+float(i)*Intensity/float(Samples/2)*(Direction+NoiseStrength*Noise));
    Color += texture(Texture,UV-float(i)*Intensity/float(Samples/2)*(Direction+NoiseStrength*Noise));  
    }    
    return Color/float(Samples);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 UV = fragCoord.xy / iResolution.yy * vec2(1.0,-1.0);

    vec2 Direction = vec2(-sin(iTime),cos(iTime));    

    if (iMouse.z > 0.0)
    {
        Direction = vec2(1.0,-1.0)*normalize(iMouse.xy-iResolution.xy/2.0);
    }


    vec4 Color = DirectionalBlur(UV,normalize(Direction),Intensity,iChannel0);

	fragColor = Color;
}
