void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 background = texture(iChannel1,vec2(0.0, 0.0)).rgb;

	vec2 uv = fragCoord.xy / iResolution.xy;
	// fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec4 mask = texture(iChannel0,uv);
    vec4 pixel = texture(iChannel1,uv);
    // white ?
    if(mask.rgb == vec3(0.0, 0.0, 0.0)) {
        fragColor = pixel;
    } else if(mask.rgb == vec3(1.0, 1.0, 1.0)) {
    // noir et blanc
        float lum = (pixel.r + pixel.b + pixel.g) / 5.0;
        fragColor = vec4(lum, lum, lum, pixel.a);
    } else {
        // couleur
        if(pixel.rgb == background) {
           //fragColor = pixel + mask;
            fragColor = mask;
        } else {
            fragColor = pixel;
        }
        //fragColor = vec4(background.r,background.g,background.b,1.0) ;

    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;
    if(xy.y < 0.5) {
       fragColor = vec4(1.0,1.0,1.0,1.0);        
    } else if(xy.y < 0.8) {
        fragColor = vec4(0.0,0.0,0.0,1.0);        
    } else {
        fragColor = vec4(iMouse.y,0.0,1.0,1.0);        
    }


}
