void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv = uv - 0.5;
    uv.y *= iResolution.y / iResolution.x;

    float pi = 3.1415926;

    fragColor = vec4(0, 0, 0, 0);
    for (int i = 0; i < 63; i++){
        float t = iTime / 5.0 + float(i) * 0.1;
    	vec2 p = 0.2 * vec2(cos(t), sin(t)) * (sin(t * 8.0) * 0.5 + 0.5);
		fragColor += vec4(0.002 / length(uv - p) 
                          * vec3(sin(t) * 0.5 + 0.5,
                                 sin(t + pi * 2.0 / 3.0),
                                 sin(t - pi * 2.0 / 3.0)),1.0);
	}
}
