void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

#define time iTime

float bob(in vec2 coords) {
    return 1.0/(dot(coords, coords) + 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    vec3 col = vec3(bob((uv - vec2(sin(time*3.0), cos(time*5.0))) * 20.0));
	fragColor = vec4(col/3.0,0.01);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 prev = texture(iChannel1, uv).rgb;
    vec4 acol = texture(iChannel0, uv);
    float alpha = acol.a;
    vec3 bias = vec3(1.0,0.999,0.99);
    fragColor = vec4(acol.rgb + prev*(1.0-alpha)*bias, 1.0);

}
