const float bulgeRadius = 0.5, bulgeStrength = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 r = iResolution.xy;
    vec2 c = iMouse.xy / r.y;

    vec2 uv = fragCoord / r.y;
    uv -= c;                // centered coordinates
    float l = length(uv);   // polar coordinates
    float d = smoothstep(0.,1., l / bulgeRadius );
    d = pow(d, bulgeStrength);
    float a = atan(uv.y, uv.x);
    uv = l* d * vec2(cos(a),sin(a));
    fragColor = texture(iChannel0, (uv+c) *r.y/r );
}
