vec3 computeColor(vec2 p ) {

    p -= vec2(0.5, 0.6);
    p*= 5.0;
	float r = sqrt(dot(p,p));
    float a = atan(p.y, p.x)+ iTime *0.1;
    float s = 0.5+ 0.5*sin(3.0*a);
    float t = 0.15+0.4*pow(s,0.5);
    t += 0.15*pow(0.5+0.5*cos(6.0*a), 0.5); 
    float h= r/t;

    float f = 0.0;
    if( h<1.0) f = 1.0;
    return mix(vec3(1.0), vec3( 0.0, 1.0, 0.0), f);

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = 0.6 * (2.0 * fragCoord.xy - iResolution.xy)/min(iResolution.y, iResolution.x);
    vec2 c = (q - vec2(-0.5, -1.0))*.5;
   float a = atan(q.x, q.y);
   float r = length(q);
    float s= 0.5 + 0.5*sin(3.0*a + iTime);
     float g = sin(1.57+3.0*a +iTime);
    float d = 0.15 + 0.3*sqrt(s) + 0.15*g*g;
    float h = r/d;
    float f = 1.0 - smoothstep(0.95 , 1.0, h);
    h *= 1.0-0.5*(1.0-h)*smoothstep(0.95+0.05*h,1.0, sin(3.0*a+ iTime));

	 vec3 bcol = vec3(0.9+.1*q.y, 1.0,0.9- 0.1*q.y);
    bcol *= 1.0 - 0.5*r;
    h = 0.1 +h ;
    vec3 col = mix(bcol, 1.2*vec3(0.6*h, 0.2+ 0.5* h , 0.0), f);
    col *= computeColor( c);
	fragColor = vec4(col,1.0);
}
