renderpass.code:
float W( vec2 U ) {
    U = 2.* mod(U*5.,vec2(1,1.4)) - vec2(1,1.9);           // tiling
    float l = length(U);
    l = U.y/l > -.7 ?  l                                   // loop top = 3/4 ring
                    :  length( U - vec2 (sign(U.x),-1) );  // loop bottom = 2x half 3/4 ring

    // one loop  // thread r= .7 thickness= .2   // pseudo-depth
    return smoothstep(.1,.0, abs(l-.7) -.1) * ( .5+.4*abs(U.y+.6) );    // face A
  //return smoothstep(.1,.0, abs(l-.7) -.1) * ( 1.-.3*abs(U.y+.6) );    // face B 
  //return abs(l-r);
}

void mainImage( out vec4 O,  vec2 U )
{
	U /= iResolution.y; 

    O += max( W(U), W( U+vec2(0,.7) ) ) -O; // inter-weaving

    O.x==W(U) ? O.bg*=.8 : O.rg *= .8; // color ( + 28 chars) 
}
