void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 metaballs[3]; 
    metaballs[0] = vec3(sin(iTime) * 0.1 + 0.1, cos(iTime) * 0.1 + 0.4, 0.05);
	metaballs[1] = vec3(1.0, 0.5, 0.1);
    metaballs[2] = vec3(iMouse.xy / iResolution.y, 0.1);

    vec2 ssnormal = fragCoord.xy / iResolution.y;

    float frag = 0.0;
    float d = 0.0;
    for(int i = 0; i < 3; i++) {
    	d += metaballs[i].z / distance(metaballs[i].xy, ssnormal);
        frag += smoothstep(0.97, 1.0, length(d));
    }

	fragColor = vec4(frag, frag, frag, 1.0);
}
