void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = .85*fragCoord.xy / iResolution.xy;
    if(uv.x > .2) {
        if(uv.x < .4) {
        	uv.y += .05;
        } else if(uv.x < .6) {
         	uv.y += .1;   
        } else if(uv.x < .8) {
         	uv.y += .15;   
        } else if(uv.x < 1.) {
        	uv.y += .25;
        }
    }
    fragColor = texture(iChannel0, uv);
}

