vec4 hsv_to_rgb(float h, float s, float v, float a)
{
	float c = v * s;
	h = mod((h * 6.0), 6.0);
	float x = c * (1.0 - abs(mod(h, 2.0) - 1.0));
	vec4 color;

	if (0.0 <= h && h < 1.0) {
		color = vec4(c, x, 0.0, a);
	} else if (1.0 <= h && h < 2.0) {
		color = vec4(x, c, 0.0, a);
	} else if (2.0 <= h && h < 3.0) {
		color = vec4(0.0, c, x, a);
	} else if (3.0 <= h && h < 4.0) {
		color = vec4(0.0, x, c, a);
	} else if (4.0 <= h && h < 5.0) {
		color = vec4(x, 0.0, c, a);
	} else if (5.0 <= h && h < 6.0) {
		color = vec4(c, 0.0, x, a);
	} else {
		color = vec4(0.0, 0.0, 0.0, a);
	}

	color.rgb += v - c;

	return color;
}
vec2 st;
float starDist(in vec2 off) {

    vec2 starST = st - vec2(0.500,0.460)+off;

    starST = starST*(1.+1.176*abs(sin(atan(starST.x,starST.y)*2.5+iTime*0.9040)));
    starST = abs(starST);

    return length(starST);
}

#define pi 3.141
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    st = fragCoord.xy/iResolution.xy;
    st.x-=0.25;
    st.x *= iResolution.x/iResolution.y;
    vec3 color = vec3(1.);



    float low = 0.144+0.00002*abs(sin(iTime*4.));
    float range = 0.324;
    float d = 0.452/100.;
    float mixVal = smoothstep(low, low+range, starDist(vec2(0.000,-0.000)));
	float offX = smoothstep(low, low+range, starDist(vec2(d,-0.000)));
	float offY = smoothstep(low, low+range, starDist(vec2(0.000,d)));
	float angle = (atan(mixVal-offX, mixVal-offY)+pi)/2./pi;    




    vec3 color1 = vec3(0.970,0.959,1.000);
    vec3 color2 = vec3(0.350,0.340,0.318);
    color = mix(color1, color2, mixVal);
	fragColor.rgb = color;
    fragColor.a = 1.;
   fragColor.rgb = pow(vec3(fract(angle*3.)),4.680*vec3(0.268,0.607,1.000));
    fragColor *=smoothstep(1., 1.-0.098, mixVal);
}
