//Number of scanlines
//Set to low value like 8 to see how it works
#define N 240.0
#define PI 3.14159265358979323

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    float scanLineNumber = floor(uv.y*N);
    float scanLineBrightness = sin(fract(uv.y*N)*PI);

    float inputBrightness = length(texture(iChannel0, vec2(uv.x, scanLineNumber/N)).rgb);
    fragColor = vec4(vec3(inputBrightness, inputBrightness*0.74, 0.0), 1.0)*scanLineBrightness;
}
