/*
 To fix our visual human system :)
	A perceptual color space ....

*/
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/vec2(iResolution.x,iResolution.y);

    vec3 rgbtex = texture(iChannel0,uv).rgb ;///vec3(255.0);


    // If you aim to process a Texture or if you like JCVD
    float gray_level = 0.2989 * rgbtex.r + 0.5870 * rgbtex.g + 0.1140 * rgbtex.b;

    //to visualize the spectrum 
    //float gray_level = fragCoord.x/iResolution.x;

    const int array_size=10;
    vec3 color[array_size] = vec3[](
        vec3(0.0,0.0,0.5),
        vec3(0.0,0.0,1.0), //blue
        vec3(0.0,0.5,1.0),
        vec3(0.0,1.0,1.0),
        vec3(0.0,1.0,0.5),
        vec3(0.5,1.0,0.0), //green
        vec3(1.0,1.0,0.0),
        vec3(1.0,0.5,0.0),
        vec3(1.0,0.0,0.0), //red
        vec3(0.5,0.0,0.0)
    );  

    float select_array = float(array_size)-1.0;

    int index = int(gray_level*select_array);

    vec3 outColor = mix(color[index],color[index+1],gray_level*select_array - float(index));

    fragColor = vec4(outColor,1.0);

}
