void mainImage( out vec4 o, in vec2 fc ) {
    vec2 uv = fc/iResolution.xy;
    vec2 mid = uv - vec2(0.5);   

    float a = 0.5 + atan(mid.y, mid.x)/6.28;
    float t = fract(iTime * 0.01);
    float l = length(mid)*0.2+0.4;

    vec3 col = texture(iChannel0, vec2(t, a)).rgb;
    float n = 1.0 - smoothstep(col.r*0.3, col.r*0.5, l)
        + smoothstep(col.r*0.5, col.r*0.7, l);
    o = vec4(mix(col, vec3(0.01, 0.02, 0.08), n), 1.0);
}
