void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2=uv;
    vec2 uv3=uv;
    uv2.x=uv2.x+(0.1*sin(iTime+uv2.y*8.18967612));
    uv2.y=uv2.y+(0.3*sin(iTime+uv2.x*3.89781211));
    uv3.x=1.0-uv2.x;
    uv3.y=1.0-uv2.y;
    float fact=1.0-sqrt(pow(uv.x-0.5,2.0)+pow(uv.y-0.5,2.0))+uv2.x*uv2.y*sin(iTime);
    fragColor=fact*texture(iChannel0,uv2)+(1.0-fact)*texture(iChannel1,uv3);
}
