// Portiert von https://github.com/MzHub/gpuakf/blob/master/glsl/kuwahara.glsl

// Radius 1 -> 3x3 Filter, Radius 2 -> 5x5 Filter, ... Radius n -> (2n+1)x(2n+1) Filter
const int radius = 2;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    // Bildkoordinaten des aktuellen Pixels -> (u,v) von 0 .. 1
    vec2 src_size = vec2 (iResolution.x, iResolution.y);
    vec2 uv = fragCoord.xy / src_size;

    // Anzahl der Pixel einer Region
    float n = float((radius + 1) * (radius + 1));

    // Summen und Summenquadrate der Regionen (in Burger: (17.4 S_1,k, 17.5 S_2,k)
    // Zu beachten: Jeweils Vektoren mit 3 Elementen f체r die einzelnen Farbkan채le RGB
    vec3 m[4];
    vec3 s[4];
    for (int k = 0; k < 4; ++k) {
        m[k] = vec3(0.0);
        s[k] = vec3(0.0);
    }

    // Werte f체r die vier Regionen berechnen

	for (int j = -radius; j <= 0; ++j)  {
        for (int i = -radius; i <= 0; ++i)  {
            vec3 c = texture(iChannel0, uv + vec2(i,j) / src_size).rgb;
            m[0] += c;
            s[0] += c * c;
        }
    }

    for (int j = -radius; j <= 0; ++j)  {
        for (int i = 0; i <= radius; ++i)  {
            vec3 c = texture(iChannel0, uv + vec2(i,j) / src_size).rgb;
            m[1] += c;
            s[1] += c * c;
        }
    }

    for (int j = 0; j <= radius; ++j)  {
        for (int i = 0; i <= radius; ++i)  {
            vec3 c = texture(iChannel0, uv + vec2(i,j) / src_size).rgb;
            m[2] += c;
            s[2] += c * c;
        }
    }

    for (int j = 0; j <= radius; ++j)  {
        for (int i = -radius; i <= 0; ++i)  {
            vec3 c = texture(iChannel0, uv + vec2(i,j) / src_size).rgb;
            m[3] += c;
            s[3] += c * c;
        }
	}

    // Region mit der kleinsten Varianz finden und deren Mittelwert als neuen Pixelwert nutzen

    float min_sigma2 = 100.0;
    for (int k = 0; k < 4; ++k) {

        // Tats채chliche Mittelwerte und Varianzen der Regionen berechnen
        // Analog zu (17.3) in Burger
        m[k] /= n;
        s[k] = abs(s[k] / n - m[k] * m[k]);

        // "Totale Varianz"
        float sigma2 = s[k].r + s[k].g + s[k].b;

        if (sigma2 < min_sigma2) {
            min_sigma2 = sigma2;
            // Neuen Pixelwert setzen
            fragColor = vec4(m[k], 1.0);
        }
    }
}
