float rand(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.0-2.0*u);

    float res = mix(
        mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
        mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
    return res*res;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float l = length(uv-0.5);
    uv+=iTime*10.0*l;
    uv/=l;
    vec2 uvn = uv / vec2(3, 1);
    float n = noise(uvn*10.0);
    n+=noise(uvn*7.5);
    n+=noise(uvn*5.0);
    n+=noise(uvn*2.5);
    n/=4.0;
	fragColor = (vec4(0, .7, 1, 1)*n*l)+texture(iChannel0, 2.*(fragCoord.xy / iResolution.xy-.5))*(1.0-clamp(l+0.9, 0.0, 1.0));
}
