////
/// Look behind you, a three headed monkey!
///madeBy@mmgs 2016



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col = vec3(0,0,-0.3*uv.y+1.9-uv.y-uv.y*.5);

    vec3 tex =  texture(iChannel0,uv*10.5).xyz+texture(iChannel0,uv*11.5).xyz;
  //  if(uv.y -1.0*uv.x+uv.x*uv.x<.5) col = vec3(0,0.8,0);
        vec3 music = texture(iChannel3,uv).xyz;
    //stars
    if(uv.y>0.6)
    {
         if(cos(uv.x*24.)>0.99993 && cos(uv.y*24.)>0.995
           ||cos(uv.x*24.)>0.99 && cos(uv.y*24.)>0.9998)        
        col+=vec3(0.+music.x,+music.x,0)*0.8;

                 if(cos(uv.x*26.)>0.99993 && cos(uv.y*26.)>0.995
           ||cos(uv.x*26.)>0.99 && cos(uv.y*26.)>0.9998)        
        col+=vec3(.0+music.y,0.+music.y,0.3)*0.8;

                 if(cos(uv.x*23.)>0.99993 && cos(uv.y*23.)>0.995
           ||cos(uv.x*23.)>0.99 && cos(uv.y*23.)>0.9998)        
        col+=vec3(.0+music.z,.0+music.z,0.5)*0.8;

         if(tex.x<0.7)
        col=vec3(uv.y*0.5);

    }
    vec3 cloud;
     //clouds
    if(uv.y>0.2)
    {
       col*=1.5*uv.y;

        vec2 p1 = vec2(0.8);
        float r = 0.2;
        r+=cos(uv.y*12.+0.2*sin(uv.x*11.+iTime*0.01))+sin(uv.x*1.5+iTime*0.01)-cos(uv.x*4.+iTime*0.01)*0.1;
        if(length(uv-p1)-r<0.) col = col+uv.y*.1;


       p1 = vec2(0.9);
        r = 0.6;
        r+=cos(uv.y*12.+0.2*sin(uv.x*16.+iTime*0.02))+sin(uv.x*4.5+iTime*0.01)-cos(uv.x*4.+iTime*0.01)*0.1;
        if(length(uv-p1)-r<0.) col = col*1.+uv.y*.4;

         p1 = vec2(0.9);
        r = 0.6;
        r+=cos(uv.y*12.+0.2*sin(uv.x*16.+iTime*0.02))+sin(uv.x*4.5+iTime*0.01)-cos(uv.x*4.+iTime*0.01)*0.1;
        if(length(uv-p1)-r<0.) col = col*1.-uv.y*.2;

          p1 = vec2(0.4);
        r = 0.1;
        r+=cos(uv.y*12.+0.2*sin(uv.x*26.+iTime*0.02))+sin(uv.x*5.5+iTime*0.06)-cos(uv.x*4.+iTime*0.01)*0.1;
        if(length(uv-p1)-r<0.) col = col*1.+uv.y*.34;
        cloud= col;
    }

    //
    /////////////////terrain

   	uv.x+=0.3;
     if(0.4+uv.y -0.9*uv.x+(uv.x*uv.x)*0.6+cos(uv.x*30.-uv.x*22.+cos(uv.x*25.))*0.05<.5) col = vec3(0.1,0.1,0.5)+tex*0.01;
    if(0.4+uv.y -0.9*uv.x+(uv.x*uv.x)*0.65+cos(uv.x*30.-uv.x*22.+cos(uv.x*25.))*0.05<.5) col = vec3(0.1,0.11,0.4)+tex*0.01;

    if(uv.y<0.6)
    {
   	   uv.x-=0.3;
       if(0.2+uv.y -0.8*uv.x+(uv.x*uv.x)*.8+cos(uv.x*30.-uv.x*23.)*0.2<.4) col = vec3(0.1,0.12,.35)+tex*0.01;
    	uv.x-=0.2;
       if(0.2+uv.y -1.3*uv.x+(uv.x*uv.x)*3.5+cos(uv.x*30.-uv.x*16.)*0.05<.6) col = vec3(0.1,0.13,0.34)+tex*0.01;


 	   if(uv.y - uv.x+(0.18+uv.x*(uv.x-0.5)*3.+cos(uv.x*10.-uv.x*22.)*0.04)*2.<.3)  col = vec3(0.1,0.14,.45)+tex*0.01;
       if(uv.y - uv.x+(0.2+uv.x*(uv.x-0.5)*2.9+cos(uv.x*29.-uv.x*18.)*0.05)*2.<.3)  col = vec3(0.1,0.15,0.35)+tex*0.01;
    }
    uv.x+=0.1;


    /////////////////////////

          /////// Road
     if(uv.y<0.61 &&uv.y>0.1)
     {
         uv.x-=0.1-uv.y*0.23*cos(uv.y*10.2);
         uv.y-=0.1;
     		if(uv.x <0.34-cos(-uv.y*45.-uv.y)*0.09-0.02+uv.y*0.09 &&
              uv.x >0.335-cos(-uv.y*45.-uv.y)*0.09-0.02+uv.y*0.09) col*=0.5+vec3(1.);

         if(uv.x <0.34-cos(-uv.y*45.-uv.y)*0.09-0.02+uv.y*0.09 &&
              uv.x >0.335-cos(-uv.y*2.-uv.y)*0.09-0.03+uv.y*0.09) col*=0.95;


         // col=vec3(1);
     }
    //////////
   //water
      uv=fragCoord.xy / iResolution.xy;
    if(uv.y<0.2+cos(uv.x*22.+sin(uv.y*222.)*2.)*0.02 - uv.y*2.-sin(uv.x*3.)*0.2)
        col=0.5+vec3(0.2,0.2,0.8);

    //


  ///////// outpost
      uv=fragCoord.xy / iResolution.xy;
        uv.x-=0.51;uv.y-=0.15;
    if(uv.y>0.46 && uv.y -.0*uv.x+uv.x*uv.x*90.<.50 ) col= tex*0.5 +cos(iTime*21.)*0.02-uv.x*5.;
   		uv.y+=0.006;
     if(uv.y>0.46 && uv.y +.22*uv.x+uv.x*uv.x*200.<.5 ) col = vec3(0,0.13,.9)+cloud;

      uv=fragCoord.xy / iResolution.xy;
    if(uv.y>0.6 && uv.y<0.613 && uv.x<0.54 && uv.x >0.45
      ||uv.y>0.6 && uv.y<0.64 && uv.x<0.47 && uv.x >0.45) col= vec3(0.2,0.2,.4)*0.5+tex*0.1 +cos(iTime*21.)*0.01;
     uv.x-=0.03;
   //  if(uv.y>0.61 && uv.y<0.62+cos(iTime*2.)*0.001+uv.x*0.0001 && uv.x<0.44-+cos(iTime)*0.00 && uv.x >0.45) col+=vec3(.35,0,0);

     if(uv.y>0.61 && uv.y<0.62+cos(iTime*4.0)*0.001 && uv.x<0.47-cos(iTime*5.)*0.001 && uv.x >0.46) col+=vec3(1.5,0.2,0.26)*1.5;
    /////////// 

    //melee town


    if(uv.y<0.25 && uv.y>0.04 && uv.x <0.45 && uv.x>0.26)
    {

        vec3 tex2 = texture(iChannel0,uv*1.5).xyz*vec3(1,1,0);
        uv.y-=0.1;
            tex=texture(iChannel1,uv*2.9).xyz*0.3;
        if(tex.x>0.20){ col -= .12-tex;if(tex2.x>0.88)  col+=tex2;}
        uv=fragCoord.xy / iResolution.xy;
        tex=texture(iChannel1,uv*3.).xyz*0.3;
        if(tex.x>0.2) {col -= .12-tex;  if(tex2.x>0.85)  col+=tex2;}

	    tex2 = texture(iChannel0,uv*2.5).xyz*vec3(1,1,0);
        uv.y-=0.1;uv.x-=0.3;
            tex=texture(iChannel1,uv*2.9).xyz*0.3;
        if(tex.x>0.20){ col -= .12-tex;if(tex2.x>0.88)  col+=tex2;}
        uv=fragCoord.xy / iResolution.xy;
        tex=texture(iChannel1,uv*3.).xyz*0.3;
        if(tex.x>0.2) {col -= .12-tex;  if(tex2.x>0.85)  col+=tex2;}
    }

    vec2 o = uv.xy*.5-0.25;
    o.x*=1.5;

    //post
    if(sin(uv.y*2222.+uv.x*2.)<0.5) col*=.95;
	     col*=0.5+uv.y*0.9;

	fragColor = vec4(col,1.0);
}
