bool FADE_MODE = true;
const float LOOPS = 6.;

float square(vec2 uv) {
    uv = fract(uv);
    return float(uv.x>(0.25)&&uv.x<(0.75)&&uv.y>(0.25)&&uv.y<(0.75));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = fract(iTime*.5);
    float c = 0.;
    float a = pow(4.,t);
    for (float i = 0.; i < LOOPS+1.; i++) {
        if (FADE_MODE) {
            if (i == LOOPS) {
                c += square((uv/a)*pow(2.,i*2.))*t;
            } else {
                c += square((uv/a)*pow(2.,i*2.));
            }
        } else {
            c += square((uv/a)*pow(2.,i*2.));
            if (t < .5 && i == LOOPS-1.) {
                break;
            }
        }

    }
    fragColor = vec4(vec3(1.-c), 1. );
}
