const int N = 360;
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x, iResolution.y);
    vec3 destColor = vec3(0.0);
    float t = sin(iTime)/5.0;
    for(int i=0; i<N; i++) {
        vec2 v = vec2(cos(radians(t+float(i))), sin(radians(t+float(i))))*sin(((1.0-cos(radians(iTime*10.0)))*float(i))/10.0);
        v *= 0.8;
        destColor += 0.0015/length(uv-v);
    }
    destColor -= sin(vec3(-1.0,0,1.0)*3.141592*2.0/3.0+iTime*2.0)*0.5+1.0;
    fragColor = vec4(destColor, 1.0);
}
