// For procedural patterns and sound there is a classical trap: 
// a space (or time) varying parameter can gives ugly fast changes (top)
// instead of what you expect. 
// Indeed, you need to integrate the "phase" to get the right thing  (bottom). 
// see also https://www.desmos.com/calculator/7dbitjcfrf
//      and https://www.shadertoy.com/view/ldGXWc

#define f(x) ( 6.  + 4.*sin(x/5.)    )   // space varying frequency. is also phase derivative
#define F(x) ( 6.*x -4.*cos(x/5.)*5. )   // its integration (i.e. absolute phase).
                                         // noise (or sin for sound) is then just a post LUT.

void mainImage( out vec4 O, vec2 U )
{
	vec2 R = iResolution.xy;
    U = (U+U-R)/R.y; 
    float M = length(iMouse.xy/R)<.01 ? 8.*iTime 
                                      : iMouse.x;
    O -= O; if (abs(U.y)<.01) return;
    float x = 20.*U.x + M;

                            // draw pattern
	O = sin( U.y>0. ? f(x)*x     // wrong intuition: varying frequency * x
                    : F(x)       // good solution: real phase = int (Dphase). Dphase = freq
           ) +O-O;
/*
    O = .2/abs(             // draw function
        sin( U.y>0. ? f(x)*x     // wrong intuition: varying frequency * x
                    : F(x)       // good solution: real phase = int (Dphase). Dphase = freq
           ) - (2.*fract(U.y)-1.)/.6) +O-O;
*/
}
