void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord -= 0.5*iResolution.xy;
	vec2 uv = fragCoord.xy / iResolution.xy;


    vec4 mask = texture(iChannel1, uv);
    float sum = mask.x+mask.y+mask.z/5.+0.3;

    vec4 mask2 = texture(iChannel2, uv);
    float sum2 = mask2.x+mask2.y+mask2.z;


	float t = sin(iTime);
    vec2 uv2 = mat2(cos(sum*t),-sin(sum*t)+2.,sin(sum*t)+2.,cos(sum*t))*uv;

    vec4 color = vec4(0.14, 0.24, 0.31, 1.)*sum2*cos(t);

    vec4 tex = texture(iChannel0, uv2-0.5);
	fragColor = vec4(mix(tex, color *(sin(t)*sum2),  0.5));
}
