// noise colors example #7; played by gigatron 
#define spec 6.

float rand(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.0-2.0*u);

    float res = mix(
        mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
        mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
    return res*res;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t=iTime;
	vec3 r = texture(iChannel0, uv-noise(uv/1.0-(t*0.2)) ).rgb;

// less code !		 
		r.r += noise(uv*spec+sin(-t));
		r.r = float(int(r.r * 4.0)) /2.0;

        r.g += noise(uv*spec+sin(t)) ;
		r.g = float(int(r.g * 4.0)) / 2.0;

        r.b += noise(uv*spec+sin(-t)) ;
		r.b = float(int(r.b * 4.0)) /2.0;




	fragColor = vec4(r, 1.0)*0.5;
}
