    vec4 rightX0 = g1 * vec4(G(iC0, wrap(uv + o0 * ix * i1.x)).w, G(iC0, wrap(uv + o0 * ix * i1.y)).w, G(iC0, wrap(uv + o0 * ix * i1.z)).w, G(iC0, wrap(uv + o0 * ix * i1.w)).w); 
