void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 guv = fragCoord/iResolution.y;
    guv.y += sin(guv.x + iTime) * 0.03; //wave anim
    guv *= 6.0;

    // circle grid
    vec2 uv = fract(guv);
    uv = (uv - 0.5)*2.0;
    vec3 col = step(0.5 + sin(iTime) * 0.3,vec3(distance(vec2(0.0), uv.xy)));
    col.b = 0.5;

    // circle grid with offset
    guv += 0.5;
    uv = fract(guv);
    uv = (uv - 0.5)*2.0;
    vec3 col2 = step(0.5 + sin(iTime + 3.14) * 0.3,vec3(distance(vec2(0.0), uv.xy)));
    col2.b = 0.9;

    // layer col2 on top of col1, white is transparency
    col = mix(col, col2, step(col2.r * col2.g * col2.b, 0.0));

    // replace white with dark blue
    col = mix(vec3(0.0, 0.0, 0.2), col, step(col.r * col.g * col.b, 0.0));

    // Output to screen
    fragColor = vec4(col,1.0);
}
