void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    float zoom = 0.5 * sin(iTime * 0.3) + 0.5;
    zoom *= 1.0;
    zoom += 1.0;
    vec2 uv = fragCoord /(zoom * max(iResolution.x, iResolution.y));

    float seedTime1 = 0.2;
    float seedTime2 = 0.236;

    vec4 image = texture(iChannel0, uv + vec2(sin(iTime*seedTime1), sin(iTime * seedTime2)));
    vec4 image2 = texture(iChannel1, uv + vec2(sin(iTime*seedTime1), sin(iTime * seedTime2)));
	vec4 image3 = texture(iChannel2, uv + vec2(sin(iTime*seedTime1), sin(iTime * seedTime2)));
    vec4 image4 = texture(iChannel3, uv + vec2(sin(iTime*seedTime1), sin(iTime * seedTime2)));

    vec3 col = mix(vec3(0.125,1.0,0.125), vec3(0.125), pow((image.r+image2.r),0.5));
    col = mix(col, vec3(image4), 1.5*image3.r);
    // Output to screen
    fragColor = vec4(col, 1.0);
}
