float sphere (vec3 r, vec3 o, float radius){
    return length(o-r)-radius;
}
float ground (vec3 r){
    return r.y-1.5;
}
vec4 trace (vec3 o, vec3 r, vec3 pos, float radius){
    float t = 0.0;
    vec3 p;
    for(int i = 0; i<32; ++i){
        p = o+r*t;
        p.y = p.y-p.x*p.x*2.;
        p.y *= 1.;
        p.z+=sin(p.y*13.)*0.01;
        p.z*=1.5;
        float d = sphere(p, pos, radius);
        t += d*0.1;
    }
    return vec4(p, t);
}
vec4 traceGround (vec3 o, vec3 r){
    float t = 0.0;
    vec3 p;
    for(int i = 0; i<50; ++i){
        p = o+r*t;
        float d = ground(p);
        t += d*0.5;
    }
    return vec4(p, t);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2. - 1.;
    uv *= .5;
    uv.x *= iResolution.x / iResolution.y;
    vec2 mouse = iMouse.xy / iResolution.xy;
    mouse.x+=0.5;
    mouse.x*=3.14159;
    vec2 ray = vec2(-cos(uv.x+mouse.x), uv.y+mouse.y-1.0);//direction
    vec3 r = normalize(vec3(ray, -sin(uv.x+mouse.x)));//ray
    vec3 o = vec3(cos(mouse.x)*2., 1., sin(mouse.x)*2.);//camera
    vec3 pos = vec3(0., 0., 0.);//position of sphere
    float radius = 0.4;
    vec4 t = trace(o, r, pos, radius);
    vec4 getPixel = vec4(1.);//texture(iChannel0, vec2(t.x-mouse.x, t.y)*1.0);
    float fog = 1.0 / (1.0 + t.w*t.w*0.8);//shadow at distance
    if(sphere(t.xyz, pos, radius)>0.2) {
        t = traceGround(o, r);
        getPixel = vec4(1., 1., 0., 1.);
        //getPixel = texture(iChannel1, vec2(t.x*0.1, t.y*1.)*1.0);
        fog = 1.0 / (1.0 + t.w*t.w*0.3);//shadow at distance
    }
    vec3 fc = getPixel.xyz*fog;
    fragColor = vec4(fc, 1);
}
