float superFormula(vec2 p, float at, float bt, float m1t, float m2t, float n1t, float n2t, float n3t) {
    float a = 1.0 + 0.3 * sin(at * iTime);
    float b = 1.0 + 0.3 * sin(bt * iTime);
    float k = 5.0;
    float m1 = k + k * sin(m1t * iTime);
    float m2 = k + k * sin(m2t * iTime);
    float n1 = k + k * sin(n1t * iTime);
    float n2 = k + k * sin(n2t * iTime);
    float n3 = k + k * sin(n3t * iTime);

    float t = atan(p.y, p.x);
    float r = pow(pow(abs(cos(m1 * t / 4.0) / a), n2) + pow(abs(sin(m2 * t / 4.0) / b), n3), -1.0 / n1);
    return r - length(p);
}

vec3 blend(vec3 c) {
    vec3 r = vec3(c.y - c.z, c.z - c.x, c.x - c.y);
    return 1.0 - abs(min(r, c));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = 5.0 * (0.5 - uv.yx);

    vec3 col = blend(vec3(
        superFormula(p, 0.263742, 0.18283, 0.456234, 0.34525, 0.454732, 0.2869456, 0.6232545),
        superFormula(p, 0.192848, 0.07314, 0.234902, 0.32453, 0.545345, 0.4384753, 0.3948573),
        superFormula(p, 0.102472, 0.21847, 0.567456, 0.34574, 0.586743, 0.3275674, 0.6532344)));

    fragColor = vec4(col,1.0);
}
