int II(int i, vec2 p) {
    if(p.x < 0.|| p.x > 3.) { return 0; }
    else if(i==5) { return 972980223; }
    else if(i==4) { return 690407533; }
    else if(i==3) { return 704642687; }
    else if(i==2) { return 696556137; }
    else if(i==1) { return 972881535; }
    else { return 0; }
}

int D(vec2 p, float n) {
    int i = int(p.y);
    float f = floor( 30. - p.x - (n * 3.) );
    int b = int(exp2(f));
    i =  II(i, p) / b;
    return i - (i / 2) * 2;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    fragCoord /= 12.;
    for (float n=3.; n>-4.; n--) {
        if ((fragCoord.x -= 4.) < 3.) {
            float seconds = iDate.w / pow(10., n);
            float m = mod(seconds, 10.);
            float f = floor(m);
            int r = D(fragCoord, f);
            fragColor = vec4(r);
            break;
        }
    }
}
