            void mainImage( out vec4 fragColor, in vec2 fragCoord )
            {
				vec2 pos = fragCoord.xy/iResolution.xy-0.5;
            	float tt = -.006 * (iTime + iDate.g * iDate.b + 71.);
            	mat2 r = mat2(cos(tt/1.002),sin(tt/1.003),-sin(tt/1.005),cos(tt/1.007));

            	vec4 p0 = vec4(pos, fragCoord.xx);

            	vec4 p = abs(0.-8.*p0 ), c=p*.0;
            	p.yx *= r;
            	for (int d=2; d<10; d++) {
            		p -= .05*float(d);
            		for (int i=0;i<3;i++) p.xy=r*(p.xy-
												   sign(p.yx)*
												   vec2(1.5*cos(sin(3.24*tt)),1.5*sin(sin(3.14*tt))));
            		c += .03*p;
            	}
            	fragColor = c+.3;
            	fragColor.b = fragColor.r + fragColor.g;
				fragColor = clamp(fragColor,0.0,1.0);
            }
