vec4 lerp(vec4 a, vec4 b, float d){
	return a*(1.0-d)+b*d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	int i, j;
	vec4 sum = vec4(0);
	for(i=-4; i < 4; i++) {
		for(j=-4; j < 4; j++) {
			sum += texture(iChannel0, uv+vec2(j, i)*0.01)*0.015625;
		}
	}

	fragColor = lerp(vec4(sum.rgb,1.0), vec4(0), (sin(iTime*3.0)+1.0)/2.0)+texture(iChannel0, uv);
}
