#define RADIUS 0.25
#define SPEED 1.0
#define NUM_STRIPES 25.0
#define STRIPE_WIDTH_OVER_2 (1.0 / NUM_STRIPES / 2.0)

float iPart(float num);
bool isOdd(float num);
bool shouldPixelBeWhite(float stripeNum);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//Normalize the coordinate and offset it for its place on the circle
	vec2 normalizedCoord = fragCoord.xy / iResolution.xy;
	normalizedCoord.x += cos(iTime / SPEED) * RADIUS;
	normalizedCoord.y += sin(iTime / SPEED) * RADIUS;

	//Determine if the coordinate is part of the red center square
	bool isPixelInCenterSquareWidth = (normalizedCoord.x > (0.5 - STRIPE_WIDTH_OVER_2)) && (normalizedCoord.x < (0.5 + STRIPE_WIDTH_OVER_2));
	bool isPixelInCenterSquareHeight = (normalizedCoord.y > (0.5 - STRIPE_WIDTH_OVER_2)) && (normalizedCoord.y < (0.5 + STRIPE_WIDTH_OVER_2));
	if(isPixelInCenterSquareWidth && isPixelInCenterSquareHeight) {
		fragColor = vec4(1.0, 0.0, 0.0, 1.0);
		return;
	}

	//Otherwise, figure out if the coordinate should be black or white

	//Is its column number odd?
	float stripeNumX = (normalizedCoord.x * NUM_STRIPES);
	float colorX = 0.0;
	if(shouldPixelBeWhite(stripeNumX)) {
		colorX = 1.0;	
	}

	//Is its row number odd?
	float stripeNumY = (normalizedCoord.y * NUM_STRIPES);
	float colorY = 0.0;
	if(shouldPixelBeWhite(stripeNumY)) {
		colorY = 1.0;	
	}

	//Composite the column and row colors to make the checkerboard
	float color = clamp(colorX + colorY, 0.0, 1.0) - (colorX * colorY);

	fragColor = vec4(color, color, color, 1.0);
}

bool shouldPixelBeWhite(float stripeNum) {
	float iPartOfStripeNum = floor(stripeNum);
	return isOdd(iPartOfStripeNum);
}

bool isOdd(float num) {
	float numOverTwo = abs(num / 2.0);
	float fpartOfNumOverTwo = numOverTwo - floor(numOverTwo);
	if(fpartOfNumOverTwo > 0.25) {
		return true;	
	} else {
		return false;	
	}
}
