// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

vec2 cinv( vec2 z)  { float d = dot(z,z); return vec2( z.x, -z.y ) / d; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 1.2*(-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;

	vec2 z = p;
	float f = 4.;
	float g = 4.;
	if(p.x>.1){
		p = 1.2*(-iResolution.xy+2.0*iMouse.xy)/iResolution.y;
		z.x-= 1.5;
		z*=1.5;
	}

	for( int i=0; i<128; i++ ) 
	{

		z=cinv(z+p)+z-p;
		f = min( f, abs(dot(z-p,z-p) ));
		g = min( g, dot(z+p,z+p));
	}

	f = 1.0+log(f)/15.0;
	g = 1.0+log(g)/10.0;


	fragColor = 1.-g*abs(vec4(g,f*g,f*f,1.0));
}
