vec3 mandl(vec2 xy) {
    vec2 z = vec2(0.0, 0.0);
	vec2 squared = vec2(0.0, 0.0);
	for(float i = 0.0; i < 50.0; i++) {
		if(squared.x + squared.y >= 4.0)
			return vec3(1.0, 1.0, 1.0); // not in set
		z = vec2(squared.x - squared.y, 2.0 * z.x * z.y) + xy;
		squared = z * z;
	}
	return vec3(0.0, 0.0, 0.0); // in set
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	float scale = iResolution.y/iResolution.x;
	vec2 uv = fragCoord.xy/iResolution.xy;
	uv = (uv - 0.5) * 3.0 - vec2(0.4, 0.0);
	fragColor = vec4(mandl(uv), 1.0);
}
