//Modified from glslsandbox.com/e#20208.0

uniform float time;
uniform vec2 resolution;

// LICENSE: CC0
// *-SA-NC considered to be cancerous and non-free

const float PI = 3.14159;
vec3 hsv(float h,float s,float v) {
	return mix(vec3(1.),clamp((abs(fract(h+vec3(3.,2.,1.)/3.)*6.-3.)-1.),0.,1.),s)*v;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float r = length(uv);
    float a = atan(uv.y, uv.x)*10.0;
    a += iTime*iMouse.y/20.;
    a += cos(r*iMouse.x/10.)*50.0;

    float g = cos(mod(a, PI)*2.0)*0.5 + 0.5;

    g = smoothstep(0.0, 0.7, length(r))*g;
    fragColor = vec4(g);

    vec4 color = vec4(hsv(g,g+1.,r+1.),1);
    fragColor = color;
}
