float metaball(vec2 p, float r) {
	return r / dot(p, p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-1.0 + 2.0*fragCoord.xy / iResolution.xy)*vec2(iResolution.x/iResolution.y, 1.0);

    //timing for movements
    float t0 = sin(iTime * 1.9) * 0.96;
    float t1 = cos(iTime * 2.4) * 0.5;
    float t2 = sin(iTime * 1.4) * 0.6;

    float r = (
        metaball(p + vec2(t0, t1), 0.23) * 
        metaball(p - vec2(t1, t2), 0.33) * 
        metaball(p + vec2(t2, t0), 0.43)
    );

    //color (mouse controlled)
    vec3 col = (iMouse.z > 0.0) ? (r > 0.4 && r < 0.42) ? vec3(0.0, 1.0, 0.5) : ((r < 0.4) ? vec3(vec3(0.5, 0.4, 0.6)*smoothstep(-1.0, 1.0, p.y)) : vec3(0.2, 0.7, 0.4)*vec3(clamp(r, 0.0, 1.0))) : (r > 0.4 && r < 0.452) ? vec3(0.0, 0.8, 0.35) : vec3(0.0, 0.7, 0.2)*clamp(r, 0.0, 1.0);
    fragColor = vec4(col,1.0);
}
