vec2 fold = vec2(-0.5, -0.5);
vec2 translate = vec2(1.5);
float scale = 1.25;

vec3 hsv(float h,float s,float v) {
	return mix(vec3(1.),clamp((abs(fract(h+vec3(3.,2.,1.)/3.)*6.-3.)-1.),0.,1.),s)*v;
}

vec2 rotate(vec2 p, float a){
	return vec2(p.x*cos(a)-p.y*sin(a), p.x*sin(a)+p.y*cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = -1.0 + 2.0*fragCoord.xy/iResolution.xy;
	p.x *= iResolution.x/iResolution.y;
	p *= 0.02;
	float x = p.y;
	p = abs(mod(p, 2.0) - 1.);
	for(int i = 0; i < 32; i++){
		p = abs(p - fold) + fold;
		p = p*scale - translate;
		p = rotate(p, 3.14159/(0.10+sin(iTime*0.0022+float(i)*0.251)*0.50001+0.501));
	}
	float i = x*500.0 + atan(p.y, p.x) + iTime*0.003;
	float h = floor(i*6.0)/5.0 + 0.1;
	h += smoothstep(0.0, 0.4, mod(i*6.0/5.0, 1.0/5.0)*5.10)/5.10 - 1.1;
	fragColor=vec4(hsv(h, 1.0, smoothstep(-1.0, 3.0, length(p))), 30);
}
