//Barakooda 4/4/2017
//white rectangle

float whiteRectangle(vec2 uv)
	{
        // creating buttom left rectangle point
        vec2 bl = step(vec2(-.1),uv);


        // creating top right rectangle point
        vec2 tr = step(vec2(-.1),-uv);

        // multiplie step layers
        return tr.x * tr.y * bl.x * bl.y ;
	}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //noramelize 
	vec2 uv = fragCoord.xy / iResolution.xy;

    //window aspect
    float aspect = iResolution.y/iResolution.x;

    //axis change from 0 - 1.0 to -1.0 - 1.0
	uv = uv * 2. - 1.;



    // createing translation data.
    vec2 translate = vec2(-cos(iTime),sin(iTime));

    //Strecth y axis
    uv.y*=aspect;

    //adding translation to uv
    uv += translate*0.45;   



	//creating square    
    float color = whiteRectangle(uv);


    //final
	fragColor = vec4(vec3(color),1.);
}
