float hue_to_rgb(float m1, float m2, float h)
{
    if (h < 0.0f) h += 1.0f;
    if (h > 1.0f) h -= 1.0f;
    if (h * 6.0 < 1.0) return m1 + (m2 - m1) * h * 6.0f;
    if (h * 2.0 < 1.0) return m2;
    if (h * 3.0 < 2.0) return m1 + (m2 - m1) * (2.0f / 3.0f - h) * 6.0f;
    return m1;
}
vec3 hsl_to_rgb(float h, float s, float l)
{
    h = h < 0.0f ? 0.0f : h > 1.0f ? 1.0f : h;
    s = s < 0.0f ? 0.0f : s > 1.0f ? 1.0f : s;
    l = l < 0.0f ? 0.0f : l > 1.0f ? 1.0f : l;

    float m2 = (l <= 0.5f) ? (l * (s + 1.0f)) : (l + s - l * s);
    float m1 = l * 2.0f - m2;
    float r = hue_to_rgb(m1, m2, h + 1.0f / 3.0f);
    float g = hue_to_rgb(m1, m2, h);
    float b = hue_to_rgb(m1, m2, h - 1.0f / 3.0f);
    return vec3(r, g, b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y += cos(uv.x * 30.0 + iTime*5.0) * 0.025;
    uv.y -= 0.5;
    uv.y = abs(uv.y);
    uv.y += 0.5;

    vec4 color = texture(iChannel0, uv);

    float gs = (color.r + color.g + color.b) / 3.0;

    float m = mod(gs * 1.0 + iTime*0.1, 0.5);
    m *= m;
    fragColor = vec4(hsl_to_rgb(m, 0.75, 0.5), 1);
}
