const float PI = 3.1415926535897932384626433832795;

mat2 rotate2d(float angle){
    return mat2(cos(angle+iTime),-sin(angle+iTime)*mix(0.5, 0.6,-sin(iTime)),
                sin(angle+iTime)*mix(0.5, 0.7,sin(iTime)),cos(angle+iTime));
}

void rot(inout vec2 uv)
{
    uv = rotate2d( PI ) * uv;
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy / mix(0.5, 0.8, sin(iTime/1.5));
    rot(uv);
    vec3 color = texture(iChannel0, uv).rgb;
    fragColor = vec4(color, 1.0);
}
