void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col = mix(vec3(1.0, 0.4, 0.1), vec3(1.0, 0.8, 0.3), sqrt(uv.y * uv.x));
    vec2 q = uv - vec2(0.35, 0.7);

    float r = (0.2 + (abs(sin(iTime * 2.0))) / 20.0) + 0.1*cos(atan(q.y, q.x) * (10.0 * sin(iTime)*2.0) + 20.0*q.x + 1.0) * sin(iTime + 1.0);

    col *= smoothstep(r, r+0.01, length( q ));

    r = 0.022;
    r += 0.002 * cos(120.0 * q.y);
    r += exp(-50.0*uv.y);

    col *= 1.0 - (1.0 - smoothstep(r, r+0.01, abs(q.x+-(sin(iTime * 10.0) / 5.0)*sin(2.0 * q.y)))) * (1.0 - smoothstep(0.1, 0.11, q.y));

	fragColor = vec4(col,1.0);
}
