#define RADIUS 3.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   //Vertical blur

    float horizontalKernel = 0.123317;
    float pointKernel = 0.195346;
    float diagonalKernel = 0.077847;

    vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = texture ( iChannel0, uv);


    float height = 1.0/iResolution.y;  
    vec4 pixel = vec4(0.0);
    vec4 tempColor = vec4(0.0);

    float divisor = 0.0;

    float kernelValue = 0.12;


    //calculate value of the selected pixel
    for ( float i = -RADIUS ; i <= RADIUS ; i++){   

        //select the pixel
       pixel = texture(iChannel0 , uv + vec2(0.0  , i * height));


        if ( i == 0.0){
            kernelValue = pointKernel;
        }else{
            kernelValue = horizontalKernel;
        }


        //apply weight
       tempColor += pixel * kernelValue;
        //save the amount of weight used
       divisor += kernelValue;


    }

    //apply to the actual pixel
   fragColor.xyz.rgb = vec3 ( tempColor.xyz.r/divisor,
                              tempColor.xyz.g/divisor,
                              tempColor.xyz.b/divisor);

}

#define RADIUS 3.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //horizontal blur

    float horizontalKernel = 0.123317;
    float pointKernel = 0.195346;
    float diagonalKernel = 0.077847;

    float height = 1.0/iResolution.y;  
    vec4 pixel = vec4(0.0);
    vec4 tempColor = vec4(0.0);

    float divisor = 0.0;

    float kernelValue = 0.12;


    vec2 uv = fragCoord.xy / iResolution.xy;


    fragColor = texture ( iChannel0, uv );


    //calculate value of the selected pixel
    for ( float i = -RADIUS ; i <= RADIUS ; i++){   

        //select the pixel
       pixel = texture(iChannel0 , uv + vec2(i * height  , 0.0));


        if ( i == 0.0){
            kernelValue = pointKernel;
        }else{
            kernelValue = horizontalKernel;
        }


        //apply weight
       tempColor += pixel * kernelValue;
        //save the amount of weight used
       divisor += kernelValue;


    }

    //apply to the actual pixel
   fragColor.xyz.rgb = vec3 ( tempColor.xyz.r/divisor,
                              tempColor.xyz.g/divisor,
                              tempColor.xyz.b/divisor);
}
