void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float h = iResolution.y;
    float w = iResolution.x;
    if (fragCoord.x > w/2.0) {
        if (fragCoord.y > h/2.0) {
        	fragColor = vec4(1,.5,.5,1);
        } else {
        	fragColor = vec4(.2,.5,1,1);
        }
    }
    else {
        if (fragCoord.y > h/2.0) {
        	fragColor = vec4(1,1,.3,.2);
        } else {
        	fragColor = vec4(.5,.5,1,1);
        }
    }
}
