#define BORDER_A 0.33
#define BORDER_B 0.66

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv;
	vec4 r;
	vec4 colorBlue;
	vec4 colorRed;
	vec4 colorWhite;

	colorBlue = vec4(0, 0, 1, 1);
	colorRed = vec4(1, 0, 0, 1);
	colorWhite = vec4(1, 1, 1, 1);
	uv = fragCoord.xy / iResolution.xy;
	if (uv.x < 0.33) {
		r = colorBlue;
	} else if (uv.x > BORDER_A  && uv.x < BORDER_B) {
		r = colorWhite;
	} else {
		r = colorRed;
	}
	fragColor = r;
}
